const express = require("express"); //framework facilita entornos
const app = express(); //inicializa express escritura de backend
const cors = require("cors"); //medida de seguridad navegadores
const mercadopago = require("mercadopago");
const path = require("path");

// REPLACE WITH YOUR ACCESS TOKEN AVAILABLE IN: https://developers.mercadopago.com/panel
mercadopago.configure({
	access_token: "TEST-8561234769130336-092022-7a757472821065327c4773ef87fa1776-452500242", //ingresamos nuestros datos
});


app.use(express.urlencoded({ extended: false }));
app.use(express.json());

app.use(express.static(path.join(__dirname, "../client")));
app.use(cors());

//inicia la primera ruta
app.get("/", function () {
	path.resolve(__dirname, "..", "client", "index.html");
});

//creacion de preferencias
app.post("/create_preference", (req, res) => {
  let preference = {
		items: [
			{
				title: req.body.description, //tiene un titulo
				unit_price: Number(req.body.price), //tiene un precio
				quantity: Number(req.body.quantity), //tiene una cantidad
			},
		],
		back_urls: {
			"success": "http://localhost:8080", //compra con exito
			"failure": "http://localhost:8080", //compra fallida
			"pending": "", //compra pendiente, ej pago efectivo pagofacil
		},
		auto_return: "approved",
	};

	mercadopago.preferences.create(preference)
		.then(function (response) {
		  res.json({
		    id: response.body.id,
		  });
		})
		.catch(function (error) {
		  console.log(error);
		});
});

//Configuraciones avanzadas de feedback
app.get('/feedback', function (req, res) {
  res.json({
	Payment: req.query.payment_id, //Estado del pago
	Status: req.query.status, //Estado de la compra
	MerchantOrder: req.query.merchant_order_id,
  });
});

app.listen(8080, () => {
	console.log("The server is now running on Port 8080");
});
